import React, { useState } from "react";

const ResumeBuilder = () => {
  const [resume, setResume] = useState({
    personalInfo: {
      name: "",
      email: "",
      phone: "",
      address: "",
      linkedin: "",
      github: ""
    },
    summary: "",
    experience: [],
    education: [],
    skills: []
  });

  const [activeSection, setActiveSection] = useState("personal");

  const updateResume = (section, data) => {
    setResume(prev => ({
      ...prev,
      [section]: data
    }));
  };

  const addSkill = (e) => {
    if (e.key === "Enter") {
      const newSkill = e.target.value.trim();
      if (newSkill) {
        updateResume("skills", [...resume.skills, newSkill]);
        e.target.value = "";
      }
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold text-center mb-8 text-blue-600">
          Smart Resume Builder
        </h1>

        <div className="bg-white rounded-lg shadow p-6">
          <div className="grid grid-cols-1 md:grid-cols-4 gap-6">
            {/* Sidebar */}
            <div className="md:col-span-1">
              <div className="space-y-2">
                {["personal", "summary", "experience", "education", "skills", "preview"].map((section) => (
                  <button
                    key={section}
                    onClick={() => setActiveSection(section)}
                    className={`w-full text-left px-4 py-2 rounded transition-colors ${
                      activeSection === section 
                        ? "bg-blue-500 text-white" 
                        : "bg-gray-100 hover:bg-gray-200"
                    }`}
                  >
                    {section.charAt(0).toUpperCase() + section.slice(1)}
                  </button>
                ))}
              </div>
            </div>

            {/* Main Content */}
            <div className="md:col-span-3">
              {activeSection === "personal" && (
                <div>
                  <h2 className="text-xl font-semibold mb-4">Personal Information</h2>
                  <div className="space-y-4">
                    <input
                      type="text"
                      placeholder="Full Name"
                      value={resume.personalInfo.name}
                      onChange={(e) => updateResume("personalInfo", {...resume.personalInfo, name: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <input
                      type="email"
                      placeholder="Email"
                      value={resume.personalInfo.email}
                      onChange={(e) => updateResume("personalInfo", {...resume.personalInfo, email: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <input
                      type="tel"
                      placeholder="Phone"
                      value={resume.personalInfo.phone}
                      onChange={(e) => updateResume("personalInfo", {...resume.personalInfo, phone: e.target.value})}
                      className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                  </div>
                </div>
              )}

              {activeSection === "summary" && (
                <div>
                  <h2 className="text-xl font-semibold mb-4">Professional Summary</h2>
                  <textarea
                    value={resume.summary}
                    onChange={(e) => updateResume("summary", e.target.value)}
                    placeholder="Write your professional summary..."
                    className="w-full h-40 p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
                  />
                </div>
              )}

              {activeSection === "skills" && (
                <div>
                  <h2 className="text-xl font-semibold mb-4">Skills</h2>
                  <input
                    type="text"
                    placeholder="Add a skill and press Enter"
                    onKeyPress={addSkill}
                    className="w-full p-3 border border-gray-300 rounded focus:outline-none focus:ring-2 focus:ring-blue-500 mb-4"
                  />
                  <div className="flex flex-wrap gap-2">
                    {resume.skills.map((skill, index) => (
                      <span key={index} className="bg-blue-100 text-blue-800 px-3 py-1 rounded-full text-sm">
                        {skill}
                      </span>
                    ))}
                  </div>
                </div>
              )}

              {activeSection === "preview" && (
                <div>
                  <h2 className="text-xl font-semibold mb-4">Resume Preview</h2>
                  <div className="border-2 border-gray-300 p-6 rounded bg-white">
                    <h1 className="text-2xl font-bold">{resume.personalInfo.name || "Your Name"}</h1>
                    <p className="text-gray-600">
                      {resume.personalInfo.email} {resume.personalInfo.phone && "| " + resume.personalInfo.phone}
                    </p>

                    <div className="mt-6">
                      <h2 className="text-xl font-semibold border-b-2 border-gray-300 pb-2">Summary</h2>
                      <p className="mt-2">{resume.summary || "Your professional summary will appear here."}</p>
                    </div>

                    {resume.skills.length > 0 && (
                      <div className="mt-6">
                        <h2 className="text-xl font-semibold border-b-2 border-gray-300 pb-2">Skills</h2>
                        <div className="flex flex-wrap gap-2 mt-2">
                          {resume.skills.map((skill, index) => (
                            <span key={index} className="bg-gray-100 text-gray-800 px-3 py-1 rounded text-sm">
                              {skill}
                            </span>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResumeBuilder;
