import React, { useState } from "react";
import axios from "axios";

const AISuggestions = ({ section, content }) => {
  const [suggestion, setSuggestion] = useState("");
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const getAISuggestion = async () => {
    if (!content || content.trim().length < 5) {
      setSuggestion("Please add more content to get meaningful AI suggestions. Write at least a sentence or two.");
      return;
    }

    setLoading(true);
    setError("");
    setSuggestion("");

    try {
      console.log("Sending AI request for section:", section);
      console.log("Content:", content);

      const response = await axios.post("http://localhost:5000/api/ai/suggest-improvements", {
        section: section,
        content: content
      });

      console.log("AI Response:", response.data);

      if (response.data.success) {
        setSuggestion(response.data.suggestion);
      } else {
        setSuggestion(response.data.suggestion);
      }
    } catch (error) {
      console.error("AI Suggestion Error:", error);
      setError("Failed to connect to AI service. Please check if the backend is running on port 5000.");
      setSuggestion("ðŸš¨ Connection Error: Make sure your backend server is running on http://localhost:5000");
    } finally {
      setLoading(false);
    }
  };

  const containerStyle = {
    backgroundColor: "#f8fafc",
    border: "1px solid #e2e8f0",
    borderRadius: "8px",
    padding: "16px",
    marginTop: "20px"
  };

  const buttonStyle = {
    backgroundColor: "#10b981",
    color: "white",
    border: "none",
    padding: "12px 20px",
    borderRadius: "6px",
    cursor: "pointer",
    width: "100%",
    marginBottom: "12px",
    fontSize: "14px",
    fontWeight: "600"
  };

  const disabledButtonStyle = {
    ...buttonStyle,
    backgroundColor: "#9ca3af",
    cursor: "not-allowed"
  };

  const suggestionStyle = {
    backgroundColor: "white",
    border: "1px solid #d1d5db",
    borderRadius: "6px",
    padding: "16px",
    fontSize: "14px",
    lineHeight: "1.6",
    minHeight: "120px",
    maxHeight: "300px",
    overflowY: "auto",
    whiteSpace: "pre-wrap"
  };

  const loadingStyle = {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    color: "#6b7280"
  };

  const errorStyle = {
    color: "#ef4444",
    fontSize: "12px",
    marginBottom: "8px"
  };

  return (
    <div style={containerStyle}>
      <h3 style={{ margin: "0 0 12px 0", fontSize: "16px", fontWeight: "600", color: "#1f2937" }}>
        ðŸ¤– AI Resume Coach
      </h3>

      {error && <div style={errorStyle}>{error}</div>}

      <button 
        onClick={getAISuggestion} 
        style={loading || !content ? disabledButtonStyle : buttonStyle}
        disabled={loading || !content}
      >
        {loading ? "ðŸ”„ Generating Smart Suggestions..." : "ðŸš€ Get AI Suggestions"}
      </button>

      <div style={suggestionStyle}>
        {loading ? (
          <div style={loadingStyle}>
            <div>AI is analyzing your content and generating professional suggestions...</div>
          </div>
        ) : suggestion ? (
          <div>
            <div style={{ fontWeight: "600", marginBottom: "8px", color: "#059669" }}>
              ðŸ’¡ Professional Suggestions:
            </div>
            {suggestion}
          </div>
        ) : (
          <div style={{ color: "#6b7280", textAlign: "center" }}>
            {!content ? "Add content and click the button to get AI-powered resume suggestions" : "Click the button to get professional AI suggestions"}
          </div>
        )}
      </div>

      <div style={{ fontSize: "12px", color: "#6b7280", marginTop: "8px" }}>
        ðŸ’¡ Tip: Write complete sentences for better AI suggestions
      </div>
    </div>
  );
};

export default AISuggestions;
