import React, { useState } from "react";
import axios from "axios";

const AISuggestions = ({ section, content }) => {
  const [suggestion, setSuggestion] = useState("");
  const [loading, setLoading] = useState(false);

  const getAISuggestion = async () => {
    if (!content) {
      setSuggestion("Please add some content first to get AI suggestions.");
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post("http://localhost:5000/api/ai/suggest-improvements", {
        section: section,
        content: content
      });
      setSuggestion(response.data.suggestion);
    } catch (error) {
      console.error("AI Suggestion Error:", error);
      setSuggestion("AI service is currently unavailable. Please try again later.");
    } finally {
      setLoading(false);
    }
  };

  const containerStyle = {
    backgroundColor: "#f8fafc",
    border: "1px solid #e2e8f0",
    borderRadius: "8px",
    padding: "16px",
    marginTop: "20px"
  };

  const buttonStyle = {
    backgroundColor: "#10b981",
    color: "white",
    border: "none",
    padding: "10px 16px",
    borderRadius: "6px",
    cursor: "pointer",
    width: "100%",
    marginBottom: "12px"
  };

  const suggestionStyle = {
    backgroundColor: "white",
    border: "1px solid #d1d5db",
    borderRadius: "6px",
    padding: "12px",
    fontSize: "14px",
    lineHeight: "1.5",
    minHeight: "80px"
  };

  return (
    <div style={containerStyle}>
      <h3 style={{ margin: "0 0 12px 0", fontSize: "16px", fontWeight: "600" }}>
        AI Suggestions ??
      </h3>

      <button 
        onClick={getAISuggestion} 
        style={buttonStyle}
        disabled={loading}
      >
        {loading ? "Getting Suggestions..." : "Get AI Suggestions"}
      </button>

      <div style={suggestionStyle}>
        {suggestion || "Click the button to get AI suggestions for your content."}
      </div>
    </div>
  );
};

export default AISuggestions;
